pragma solidity 0.5.11;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IERC1620.sol";

/**
    Workflow:
    1. User approves Inheritance contract to spend desired amount of tokens
    2. User calls Inheritance doIt() function. This function does in one transaction:
        a) transfer amount of tokens approved previously to itself
        b) approve Sablier contract to spend tokens that are now owned by Inheritance contract
        b) calls createStream() on Sablier contract. Sablier contract does:
            a) create stream where Inheritance contract is the sender (msg.sender)
            b) transfer previously approved tokens from Inheritance contract to itself

     Result:
       - A new stream for recipient is created, with the Inheritance contract as sender.
       - Since the user is not the Sender, he can not cancel the stream anymore.
       - No dangerous temporary accounts are involved
*/

contract Inheritance {

    address public Sablier;

    constructor(address _Sablier) public {
        Sablier = _Sablier;
    }

    function doIt(address recipient, uint256 deposit, address tokenAddress, uint256 startTime, uint256 stopTime)
        public
        returns (uint256)
    {
        // Note - Not doing any input validation here, as this is done anyway in the Sablier contract

        // transfer tokens to me. Will fail if not approved.
        require(IERC20(tokenAddress).transferFrom(msg.sender, address(this), deposit), "token transfer failure");

        // approve Sablier to spend the tokens I just received
        require(IERC20(tokenAddress).approve(Sablier, deposit));

        // create stream on Sablier contract and return the streamID
        return IERC1620(Sablier).createStream(recipient, deposit, tokenAddress, startTime, stopTime);
    }
}
